mapper.select:
SELECT a.meal_code , a.member_id , a.food_date , a.food_time , a.feedback , b.FOOD_GP , b.food_cd , b.meal_code , b.food_quality , c.food_name , c.calorie , c.crabs , c.protein , c.fat FROM diet a , FOOD b , FOOD_APi c WHERE a.meal_code = b.meal_code AND b.food_cd = c.food_cd AND a.member_id = #{memberId} AND a.food_Date = #{foodDate}
AND a.food_time = #{foodTime}
ORDER BY a.food_time

SELECT
sum(c.calorie*b.food_quality) as total_cal , sum(c.crabs*b.food_quality) as total_crabs , sum(c.protein*b.food_quality) as total_protein , sum(fat*b.food_quality) as total_fat FROM diet a , FOOD b , FOOD_APi c WHERE a.meal_code = b.meal_code AND b.food_cd = c.food_cd AND a.member_id = #{memberId} AND a.food_Date = #{foodDate}
AND a.food_time = #{foodTime}
GROUP BY a.food_time

mapper.insert:


	insert into diet
	(
	  meal_code
	, member_id
	, food_date
	, food_time
	, feedback
	)
	SELECT 

	   #{mealCode, jdbcType=VARCHAR}
    	, #{memberId, jdbcType=VARCHAR}
     	, #{foodDate, jdbcType=VARCHAR}
     	, #{foodTime, jdbcType=VARCHAR}
     	, #{feedback, jdbcType=VARCHAR}
	 FROM DUAL
     WHERE NOT EXISTS (
    SELECT 1 FROM diet WHERE meal_code = #{mealCode, jdbcType=VARCHAR}
    )
